import React from 'react';
import Header from './components/Header/Header';
import ProductList from './components/ProductList/ProductList';
import Footer from './components/Footer/Footer';
import './App.css';

// Sample product data with relevant furniture images
const sampleProducts = [
  {
    id: 1,
    name: 'Sofa Góc Hiện Đại',
    category: 'Phòng khách',
    price: 15900000,
    oldPrice: 18900000,
    image: 'https://product.hstatic.net/200000065946/product/sofa-goc-cao-cap-thanh-ly-_1__ad9b737130e0402586cc4228ebf3e446_large.jpg',
    description: 'Sofa góc hiện đại với chất liệu vải cao cấp, khung gỗ tự nhiên bền đẹp'
  },
  {
    id: 2,
    name: 'Giường Ngủ Cao Cấp',
    category: 'Phòng ngủ',
    price: 12500000,
    image: 'https://product.hstatic.net/200000065946/product/giuong-ngu-go-soi_a0e4c74eeef84d09a0f96f865f75a7f4_large.jpg',
    description: 'Giường ngủ thiết kế sang trọng, chất liệu gỗ tự nhiên cao cấp'
  },
  {
    id: 3,
    name: 'Bàn Ăn Gỗ Tự Nhiên',
    category: 'Phòng ăn',
    price: 8900000,
    oldPrice: 10900000,
    image: 'https://product.hstatic.net/200000065946/product/ban-an-go-soi_0e56593f4f6f45c0a752764df13c0d48_large.jpg',
    description: 'Bàn ăn gỗ sồi tự nhiên, thiết kế hiện đại, bền đẹp'
  },
  {
    id: 4,
    name: 'Tủ Quần Áo 4 Cánh',
    category: 'Phòng ngủ',
    price: 7500000,
    image: 'https://product.hstatic.net/200000065946/product/tu-quan-ao-go-cong-nghiep_0e56593f4f6f45c0a752764df13c0d48_large.jpg',
    description: 'Tủ quần áo 4 cánh lớn, nhiều ngăn tiện dụng'
  },
  {
    id: 5,
    name: 'Bàn Làm Việc Modern',
    category: 'Văn phòng',
    price: 4500000,
    oldPrice: 5500000,
    image: 'https://product.hstatic.net/200000065946/product/ban-lam-viec-go-cong-nghiep_0e56593f4f6f45c0a752764df13c0d48_large.jpg',
    description: 'Bàn làm việc thiết kế hiện đại, có ngăn kéo tiện dụng'
  },
  {
    id: 6,
    name: 'Kệ TV Thông Minh',
    category: 'Phòng khách',
    price: 6900000,
    image: 'https://product.hstatic.net/200000065946/product/ke-tivi-go-cong-nghiep_0e56593f4f6f45c0a752764df13c0d48_large.jpg',
    description: 'Kệ TV đa năng với nhiều ngăn để đồ, thiết kế thông minh'
  }
];

function App() {
  return (
    <div className="app">
      <Header />
      <main className="main-content">
        <ProductList products={sampleProducts} />
      </main>
      <Footer />
    </div>
  );
}

export default App;
