import React from 'react';
import ProductCard from '../ProductCard/ProductCard';
import './ProductList.css';

const ProductList = ({ products }) => {
  return (
    <div className="product-list-container">
      <div className="product-list-header">
        <h2>Sản phẩm nổi bật</h2>
        <div className="product-filters">
          <button className="filter-btn active">Tất cả</button>
          <button className="filter-btn">Phòng khách</button>
          <button className="filter-btn">Phòng ngủ</button>
          <button className="filter-btn">Phòng ăn</button>
          <button className="filter-btn">Văn phòng</button>
        </div>
      </div>
      <div className="product-grid">
        {products.map((product) => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div>
    </div>
  );
};

export default ProductList;
