import React from 'react';
import Header from './components/Header/Header';
import ProductList from './components/ProductList/ProductList';
import Footer from './components/Footer/Footer';
import './App.css';

// Import product images
import sofaImage from './assets/images/products/sofa.jpg';
import bedImage from './assets/images/products/bed.jpg';
import diningTableImage from './assets/images/products/dining-table.jpg';
import wardrobeImage from './assets/images/products/wardrobe.jpg';
import deskImage from './assets/images/products/desk.jpg';
import tvStandImage from './assets/images/products/tv-stand.jpg';

// Sample product data with real images
const sampleProducts = [
  {
    id: 1,
    name: 'Sofa Góc Hiện Đại',
    category: 'Phòng khách',
    price: 15900000,
    oldPrice: 18900000,
    image: sofaImage,
    description: 'Sofa góc hiện đại với chất liệu vải cao cấp, khung gỗ tự nhiên bền đẹp'
  },
  {
    id: 2,
    name: 'Giường Ngủ Cao Cấp',
    category: 'Phòng ngủ',
    price: 12500000,
    image: bedImage,
    description: 'Giường ngủ thiết kế sang trọng, chất liệu gỗ tự nhiên cao cấp'
  },
  {
    id: 3,
    name: 'Bàn Ăn Gỗ Tự Nhiên',
    category: 'Phòng ăn',
    price: 8900000,
    oldPrice: 10900000,
    image: diningTableImage,
    description: 'Bàn ăn gỗ sồi tự nhiên, thiết kế hiện đại, bền đẹp'
  },
  {
    id: 4,
    name: 'Tủ Quần Áo 4 Cánh',
    category: 'Phòng ngủ',
    price: 7500000,
    image: wardrobeImage,
    description: 'Tủ quần áo 4 cánh lớn, nhiều ngăn tiện dụng'
  },
  {
    id: 5,
    name: 'Bàn Làm Việc Modern',
    category: 'Văn phòng',
    price: 4500000,
    oldPrice: 5500000,
    image: deskImage,
    description: 'Bàn làm việc thiết kế hiện đại, có ngăn kéo tiện dụng'
  },
  {
    id: 6,
    name: 'Kệ TV Thông Minh',
    category: 'Phòng khách',
    price: 6900000,
    image: tvStandImage,
    description: 'Kệ TV đa năng với nhiều ngăn để đồ, thiết kế thông minh'
  }
];

function App() {
  return (
    <div className="app">
      <Header />
      <main className="main-content">
        <ProductList products={sampleProducts} />
      </main>
      <Footer />
    </div>
  );
}

export default App;
