import React from 'react';
import Header from './components/Header/Header';
import ProductList from './components/ProductList/ProductList';
import Footer from './components/Footer/Footer';
import './App.css';

// Sample product data
const sampleProducts = [
  {
    id: 1,
    name: 'Sofa Góc Hiện Đại',
    category: 'Phòng khách',
    price: 15900000,
    oldPrice: 18900000,
    image: 'https://example.com/sofa.jpg'
  },
  {
    id: 2,
    name: 'Giường Ngủ Cao Cấp',
    category: 'Phòng ngủ',
    price: 12500000,
    image: 'https://example.com/bed.jpg'
  },
  {
    id: 3,
    name: 'Bàn Ăn Gỗ Tự Nhiên',
    category: 'Phòng ăn',
    price: 8900000,
    oldPrice: 10900000,
    image: 'https://example.com/dining-table.jpg'
  },
  {
    id: 4,
    name: 'Tủ Quần Áo 4 Cánh',
    category: 'Phòng ngủ',
    price: 7500000,
    image: 'https://example.com/wardrobe.jpg'
  },
  {
    id: 5,
    name: 'Bàn Làm Việc Modern',
    category: 'Văn phòng',
    price: 4500000,
    oldPrice: 5500000,
    image: 'https://example.com/desk.jpg'
  },
  {
    id: 6,
    name: 'Kệ TV Thông Minh',
    category: 'Phòng khách',
    price: 6900000,
    image: 'https://example.com/tv-stand.jpg'
  }
];

function App() {
  return (
    <div className="app">
      <Header />
      <main className="main-content">
        <ProductList products={sampleProducts} />
      </main>
      <Footer />
    </div>
  );
}

export default App;
